import React, { useState } from 'react'

const Counter = (props) => {
  const {price}=props;
const [counter,setCounter]=useState(price);
console.log(price);

  return (
    <div>
      <button onClick={()=>setCounter(price+1)}>+</button>
      <span>{price}</span>
      <button onClick={()=>setCounter(price-2)}>-</button>
    </div>
  )
}

export default Counter;
